import react, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpensesFilter";
import "./Expenses.css";
import { findAllByDisplayValue } from "@testing-library/react";

function Expenses(props) {
  const [filteredYear, setFilteredYear] = useState("2020");
  const [expensesAfterFiltr, setExpensesAfterFiltr] = useState(props.expenses);

  const filterChangeHandller = (selectedYear) => {
    setFilteredYear(selectedYear);
    console.log(filteredYear);
    

    setExpensesAfterFiltr(() => {
      return props.expense.filter(
        (item) => item.date.getFullYear() == selectedYear
      );
    });
    console.log(expensesAfterFiltr);
  };

  return (
    <div className="expenses">
      <ExpensesFilter
        selected={filteredYear}
        onChangeFilter={filterChangeHandller}
      />

      {props.expenses.map((expense) => (
        <ExpenseItem
          key={expense.id}
          title={expense.title}
          amount={expense.amount}
          date={expense.date}
        />
      ))}
    </div>
  );
}

export default Expenses;
